// filterSlice.js
import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  userInput: {
    categoryGroup: "",
    categoryCode: "",
    colorCode: "",
    size: "",
    input: "",
  },
  selectOptions: [],
};

const filterSlice = createSlice({
  name: "filter",
  initialState,
  reducers: {
    setUserInput: (state, action) => {
      state.userInput = { ...state.userInput, ...action.payload };
    },
    setSelectOptions: (state, action) => {
      state.selectOptions = action.payload;
    },
  },
});

export const { setUserInput, setSelectOptions } = filterSlice.actions;

// Thunk for fetching select options
export const fetchSelectOptions = (csrfToken, BASE_URL) => async (dispatch) => {
  try {
    const response = await axios.get(`${BASE_URL}/api/rt/all`, {
      withCredentials: true,
      headers: {
        "X-CSRF-TOKEN": csrfToken,
      },
    });
    dispatch(setSelectOptions(response.data));
  } catch (err) {
    console.error("Error fetching select options:", err);
  }
};

export default filterSlice.reducer;
