import "./Rt.css";
import "./RtRegister.css"
import axios from "axios"
import { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";

const RtRegister = () => {
  const csrfToken = "38354D593B8BEC5217C2CF402FC25EFC"; // CSRF 토큰

  const [category, setCategory] = useState([])
  const [categorycode, setCategoryCode] = useState([])
  const [colorCode, setColorCode] = useState([])
  const [size, setSize] = useState([])
  const [products, setProducts] = useState([])
  const [store, setStore] = useState([])


  const [search, setSearch] = useState({
    categoryGroup : "",
    categoryCode : "",
    colorCode : "",
    size : "",
    input : "",
  })


  const fetchData = async (url, setData) => {
    try {
      const response = await axios.get(url, {
        withCredentials: true,
        maxRedirects: 0, // 리다이렉션 방지
        headers: {
          'X-CSRF-TOKEN': csrfToken,
        },
      });
      setData(response.data); // 데이터를 상태에 저장
    } catch (err) {
      if (err.response && err.response.status === 302) {
        console.error('리다이렉션 발생:', err.response.headers.location);
      } else {
        console.error('Error:', err);
      }
    }
  };


  // 검색 조건 담는 이벤트 함수
  const onChangeSelect = (e) => {
    setSearch({
      ...search,
      [e.target.name] : e.target.value
    })
  }

  const setAlldata = (e) => {
    fetchData('http://localhost:8080/api/rt/product ', setProducts);
  }

  useEffect(() => {
    fetchData('http://localhost:8080/api/rt/categoryGroup', setCategory);
    fetchData('http://localhost:8080/api/rt/category', setCategoryCode);
    fetchData('http://localhost:8080/api/rt/color', setColorCode);
    fetchData('http://localhost:8080/api/rt/size', setSize);
    fetchData('http://localhost:8080/api/rt/store ', setStore);


  }, []);



  const filteredData = async () => {
    try {
      // 데이터 가져오기
      const response = await axios.get('http://localhost:8080/api/rt/product', {
        withCredentials: true,
        maxRedirects: 0, // 리다이렉션 방지
        headers: {
          'X-CSRF-TOKEN': csrfToken,
        },
      });
      // 데이터 필터링
      const filtered = search.categoryGroup  || search.categoryCode  
      || search.colorCode || search.size || search.input 
      ? response.data.filter((item) => {
          return (
            (item.product_size === search.size)
          );
        })
      : response.data; // input 값이 없으면 원래 데이터 반환

      // 필터링된 데이터 상태 업데이트
      myRtSetData(filtered);
    } catch (err) {
      console.error('Error fetching or filtering data:', err);
    }
  };

  console.log(products)

  const nav = useNavigate()
  const goSearch = () => {
    return nav('../search')
  }

  return (
    <>
      <header className="searchBar">
        <div className="buttons">
          <button onClick={setAlldata}>조회</button>
          <button onClick={goSearch}>취소</button>
          <button id="" >마감처리</button>
        </div>
      </header>

      <div class="search">
        <div class="searchSelect">
          <select 
            name="categoryGroup"
            value={search.categoryGroup}
            onChange={onChangeSelect}
            >
            <option value={''}>카테고리 그룹</option>
            {category.map((item, index) => {
              return(
                <option key={index}>
                  {item.category_group_code}
                </option>
              )
              })}
          </select>
          <select 
            name="categoryCode"
            value={search.categoryCode}
            onChange={onChangeSelect}
          >
            <option value={''}>카테고리 코드</option>
            {categorycode.map((item, index) => {
              return(
                <option key={index}>
                  {item.category_code}

                </option>
              )
              })}
          </select>
          <select 
            name="colorCode"
            value={search.colorCode}
            onChange={onChangeSelect}
          >
            <option value={''}>색상 코드</option>
            {colorCode.map((item, index) => {
              return(
                <option key={index}>
                  {item.color_code}
                </option>
              )
              })}
          </select>
          <select 
            name="size"
            value={search.size}
            onChange={onChangeSelect}>
            <option value={''}>사이즈</option>
            {size.map((item, index) => {
              return(
                <option key={index}>
                  {item.size}
                </option>
              )
              })}
          </select>
        </div>
        <div className="searchInputGroup">
          <input 
            type="text" 
            className="searchInput" 
            placeholder="상품명을 입력해주세요"
            name="input"
            onChange={onChangeSelect}/>
          <button type="submit" class="search-button">검색</button>
        </div>
      </div>
      <article className="table">
        <table>
          <thead>
            <tr>
              <th className="chooseProduct">
                <input type="checkbox"/>
              </th>
              <th>상품코드</th>
              <th>상품명</th>
              <th>색상</th>
              <th>사이즈</th>
              <th>지시수량</th>
              <th>지시매장</th>

            </tr>
          </thead>  
          <tbody>
            {products.length > 0 ? (
              products.map((item, index) => (
                <tr key={item.prod_id}>
                  <td><input type="checkbox"/></td>
                  <td>{item.product_code}</td>
                  <td>{item.product_name}</td>
                  <td>{item.color_code}</td>
                  <td>{item.product_size}</td>
                  <td><input type="text" placeholder="원하는 수량을 입력해 주세요" /></td>
                  <td><section>
                    <option>매장 선택</option>
                    </section></td>
                </tr>
              ))
            ) : ( 
              <tr>
                <td className="noData">
                  데이터가 없습니다
                </td>
            </tr>
            )}        
          </tbody>    
        </table>
      </article>
    </>
  )
}

export default RtRegister
