import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchSelectOptions, setUserInput } from "../../store/filterSlice";
import useToken from "../../store/tokenManage";
import { useConfig } from "../../store";
import styles from "./InventoryShipping.module.css";

const InventoryShipping = () => {
  const { getToken } = useToken();
  const csrfToken = getToken(); // CSRF 토큰
  const { BASE_URL } = useConfig(); // 기본 URL

  const dispatch = useDispatch();
  const selectOptions = useSelector((state) => state.filter.selectOptions); // selectOptions 상태
  const userInput = useSelector((state) => state.filter.userInput); // userInput 상태

  useEffect(() => {
    dispatch(fetchSelectOptions(csrfToken, BASE_URL)); // selectOptions 데이터 로드
  }, [dispatch, csrfToken, BASE_URL]);

  const handleInputChange = (field, value) => {
    dispatch(setUserInput({ [field]: value })); // userInput 상태 업데이트
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.header}>출고</h1>
      <hr />
      <div className={styles.searchSection}>
        <form className={styles.searchForm}>
          <div className={styles.inputRow}>
            <select
              value={userInput.categoryGroup}
              onChange={(e) => handleInputChange("categoryGroup", e.target.value)}
            >
              <option value="">카테고리 그룹</option>
              {selectOptions.map((option, index) => (
                <option key={index} value={option.group}>
                  {option.groupName}
                </option>
              ))}
            </select>
            <select
              value={userInput.categoryCode}
              onChange={(e) => handleInputChange("categoryCode", e.target.value)}
            >
              <option value="">카테고리 코드</option>
              {/* 카테고리 코드 옵션들 */}
            </select>
          </div>
          <div className={styles.inputRow}>
            <select
              value={userInput.colorCode}
              onChange={(e) => handleInputChange("colorCode", e.target.value)}
            >
              <option value="">색상 코드</option>
              {/* 색상 코드 옵션들 */}
            </select>
            <select
              value={userInput.size}
              onChange={(e) => handleInputChange("size", e.target.value)}
            >
              <option value="">사이즈</option>
              {/* 사이즈 옵션들 */}
            </select>
          </div>
          <div className={styles.lastRow}>
            <input
              type="text"
              value={userInput.input}
              onChange={(e) => handleInputChange("input", e.target.value)}
              placeholder="상품 코드를 입력하세요."
            />
            <button type="submit" className={styles.searchButton}>
              검색
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default InventoryShipping;
