import styles from './InventoryShipping.module.css';

import React, { useState, useEffect } from 'react';
import axios from 'axios';

import useToken from "../../store/tokenManage";
import { useConfig } from "../../store";
import { useDispatch, useSelector } from "react-redux";
import { fetchSelectOptions } from '../../store/filterSlice';

const InventoryShipping = () => {
  const { getToken } = useToken();
  const csrfToken = getToken(); // CSRF 토큰
  const { BASE_URL } = useConfig(); // 기본 URL

  const dispatch = useDispatch();
  const selectOptions = useSelector((state) => state.filter.selectOptions); // selectOptions 상태
  const userInput = useSelector((state) => state.filter.userInput); // userInput 상태

  useEffect(() => {
    dispatch(fetchSelectOptions(csrfToken, BASE_URL)); // selectOptions 데이터 로드
  }, [dispatch, csrfToken, BASE_URL]);


  console.log(selectOptions)

  const [selectAll, setSelectAll] = useState(false);
  const [checkedItems, setCheckedItems] = useState({
    item1: false,
    item2: false,
    item3: false
  });
  const [products, setProducts] = useState([]);
  const state = useSelector( (state) => (state))











  // async func
  const fetchData = async (url, setData) => {
    try {
      const response = await axios.get(url, {
        withCredentials: true,
        headers: {
          "X-CSRF-TOKEN": csrfToken,
        },
      });
      setData(response.data);
    } catch (err) {
      console.error("Error:", err);
    }
  };

// 
  // async func
  const postTest = async () => {
    const response = await axios({
      method: "POST",
      url: `${BASE_URL}/api/retrieval/submit/admin`,
      data: { instructions: [
        {
          "productId": 2,
          "storeId": 2,
          "retrivalQuantity": 10
        }
    ]},
      withCredentials: true,
      maxRedirects: 0,
      headers: {
        "X-CSRF-TOKEN": csrfToken,
        "Content-Type": "application/json",
      },
    });
    console.log(response.data)
  }

  const handleInputChange = (field, value) => {
    dispatch(setUserInput({ [field]: value })); // userInput 상태 업데이트
  }; 

  useEffect(() => {
    fetchData(`${BASE_URL}/api/retrieval/list`,setProducts)

    // 모든 항목이 체크되었는지 확인
    const totalItems = Object.keys(checkedItems).length;
    const checkedCount = Object.values(checkedItems).filter(Boolean).length;

    // 모든 항목이 체크되었으면 전체 선택도 체크
    setSelectAll(totalItems === checkedCount && totalItems > 0);
  }, [checkedItems]);

  const handleSelectAll = (e) => {
    const isChecked = e.target.checked;
    setSelectAll(isChecked);

    // 모든 항목의 체크 상태를 변경
    const updatedCheckedItems = {};
    Object.keys(checkedItems).forEach(key => {
      updatedCheckedItems[key] = isChecked;
    });
    setCheckedItems(updatedCheckedItems);
  };

  const handleCheck = (id) => {
    setCheckedItems(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  return (
    <div className={styles.container}>
