import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css';
import axios from 'axios';
import Swal from 'sweetalert2';

function Login() {
  const BASE_URL = "http://localhost:8080"

  // define ===================================================
  const navigate = useNavigate();  // Îã§Î•∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò 

  // login ÏûÖÎ†• Ï†ïÎ≥¥ Îã¥Îäî state
  const [loginInput, setLoginInput] = useState(
    { username : "",
      password : ""
    }
  )  
  const [showPassword, setShowPassword] = useState(false)  // ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥¥Í∏∞
  const [loginCheck, setLoginCheck] = useState(false)  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú Ï≤¥ÌÅ¨


  // function ================================================

    // Î°úÍ∑∏Ïù∏ Î≤ÑÌäº
    const loginSubmit = async (e) => {
      e.preventDefault()

      if (!loginInput.username.trim() || !loginInput.password.trim()) {
        return Swal.fire({
          icon: 'error',
          title: 'ÏûÖÎ†• Ïò§Î•ò',
          text: 'ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.',
        });
      }
      try {
        // 2. ÏÑúÎ≤Ñ ÏöîÏ≤≠
        const response = await axios({
          url: `${BASE_URL}/api/user/login`,
          method: 'POST',
          data: loginInput,
        }).then()

        // 3. ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨
        if (response.status === 200) {
          // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ
          Swal.fire({
            icon: 'success',
            title: 'Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ',
            text: 'ÌôòÏòÅÌï©ÎãàÎã§!',
          });
          // Î¶¨ÎîîÎ†âÏÖò Ï≤òÎ¶¨ (Ïòà: Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô)

        } 
      } catch (err) {
        if (err.status === 401) {
           // Î°úÍ∑∏Ïù∏ Ïã§Ìå® (Ïòà: ÏûòÎ™ªÎêú ÏûêÍ≤© Ï¶ùÎ™Ö)
           Swal.fire({
            icon: 'error',
            title: 'Î°úÍ∑∏Ïù∏ Ïã§Ìå®',
            text: 'ÏïÑÏù¥Îîî ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.',
          });
        }
        // ÎÑ§Ìä∏ÏõåÌÅ¨ ÎòêÎäî ÏÑúÎ≤Ñ Ïò§Î•ò Ï≤òÎ¶¨
        Swal.fire({
          icon: 'error',
          title: 'Ïò§Î•ò Î∞úÏÉù',
          text: 'ÏÑúÎ≤ÑÏôÄ ÌÜµÏã†Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.',
        });
        console.error(err);
      }
    }




  const togglePasswordVisibility = () => {
    setShowPassword((prevState) => !prevState);
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    navigate('/signup');
  };


  return (
    <div className={styles.container}>
      <div className={styles.textContainer}>
      <h1>
          Stack<br/>
          &nbsp;&nbsp;&nbsp;&nbsp;Smarter,<br/>
          Flow<br/>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Better.
        </h1>
      </div>

      <div className={styles.formContainer}>

        <form className={styles.loginForm}>
          <div className={styles.inputGroup}>
            <label htmlFor="username" className={styles.label}>ID</label>
            <input
              type="text"
              username="username"
              placeholder="ÏïÑÏù¥Îîî"
              value={loginInput.username}
              className={styles.inputField}
              onChange={(e) => 
                setLoginInput({...loginInput, username: e.target.value})
              }
            />
          </div>

          <div className={styles.inputGroup}>
            <label className={styles.label}>Password</label>
            <div className={styles.passwordContainer}>
              <input
                type={showPassword ? "text" : "password"}
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
                className={styles.inputField}
                value={loginInput.password}
                onChange={(e) =>
                  setLoginInput({...loginInput, password: e.target.value})
                }
              />
              <span 
                className={styles.showPassword} 
                onClick={togglePasswordVisibility}
              >
                {showPassword ? 'üîí' : 'üëÅÔ∏è'}
              </span>
            </div>
          </div>
          <button 
            type="submit" 
            className={styles.loginButton}
            onClick={loginSubmit}>
              Log in
            </button>
          <div className={styles.signupContainer}>
            <span className={styles.signupText}>ÏïÑÏßÅ ÌöåÏõêÏù¥ ÏïÑÎãàÏã†Í∞ÄÏöî?</span>
            <button 
              type="button"
              className={styles.signUpButton} 
              onClick={handleSignUp}
            >
              Sign Up
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
