import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  userInput: {
    categoryGroup: "",
    categoryCode: "",
    colorCode: "",
    size: "",
    input: "",
  },
  selectOptions: [],
};

const filterSlice = createSlice({
  name: "filter",
  initialState,
  reducers: {
    setUserInput: (state, action) => {
      state.userInput = { ...state.userInput, ...action.payload };
    },
    setSelectOptions: (state, action) => {
      state.selectOptions = action.payload;
    },
  },
});

export const { setUserInput, setSelectOptions } = filterSlice.actions;

export const fetchSelectOptions = () => async (dispatch, getState) => {
  const { csrfToken, BASE_URL } = getState(); // Redux 상태에서 CSRF 토큰과 BASE_URL 가져오기
  try {
    const response = await axios.get(`${BASE_URL}/api/rt/all`, {
      withCredentials: true,
      headers: {
        "X-CSRF-TOKEN": csrfToken,
      },
    });
    dispatch(setSelectOptions(response.data)); // 데이터 저장
  } catch (err) {
    console.error("Error fetching select options:", err);
  }
};

export default filterSlice.reducer;
