import "./Rt.css";
import axios from "axios"
import { useState, useEffect } from 'react'

const Rt = () => {

  //  검색 조건 담는 변수
  const [input, setInput] = useState({
    date : "",
    check : "",
    select : ""
  })

  // 검색 조건 담는 이벤트 함수
  const onChangeInput = (e) => {
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
  }


  // 데이터 가져오는 함수 
  const [myRtData, myRtSetData] = useState([]) // 데이터를 저장할 상태
  const [otherRtData, otherRtSetData] = useState([]) // 데이터를 저장할 상태
  const [storeList, storeListSetData ] = useState([])
  const csrfToken = "AF8C8BD03BB26A5CDD7674877529F00D"; // CSRF 토큰

  // 지시요청 조회 데이터 요청 함수
  const getMyRtData = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/rt/meToOtherRtlist', {
        withCredentials: true,
        maxRedirects: 0, // 리다이렉션 방지
        headers: {
          'X-CSRF-TOKEN': csrfToken,
        },
      });
      myRtSetData(response.data); // 데이터를 상태에 저장
    } catch (err) {
      if (err.response && err.response.status === 302) {
        console.error('리다이렉션 발생:', err.response.headers.location);
      } else {
        console.error('Error:', err);
      }
    }
  }
  // 지시요청 조회 데이터 요청 함수
  const getOtherRtData = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/rt/OtherToMeRtlist', {
        withCredentials: true,
        maxRedirects: 0, // 리다이렉션 방지
        headers: {
          'X-CSRF-TOKEN': csrfToken,
        },
      });
      otherRtSetData(response.data); // 데이터를 상태에 저장
    } catch (err) {
      if (err.response && err.response.status === 302) {
        console.error('리다이렉션 발생:', err.response.headers.location);
      } else {
        console.error('Error:', err);
      }
    }
  }

  const getStoreListData = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/rt/store', {
        withCredentials: true,
        maxRedirects: 0, // 리다이렉션 방지
        headers: {
          'X-CSRF-TOKEN': csrfToken,
        },
      });
